INSERT INTO `fun_video` VALUES(17, 'Qigong: Yi Jin Jing (Ching) Chi Kung', 'http://www.youtube.com/watch?v=7TWnR9acHWo', 719, '240', 'Chen, Wu, Sun tai chi tyle and Tai Chi sword, broadsword, qigong (Chi gong)for internal energy healing, bio-energy, Shaolin and Wudang Kung-fu martial arts. Master Jesse Tsao presents the most complete set, practiced in the Beijing area, and explains each posture''s healing benefits based on traditional Chinese medicine (TCM). He also includes an adopted version of Yi Jin Ching on the chair. Detailed instructions are given in English with front and back views.', 'Non-English', 215267, 'Tutorial,Application', 'http://i.ytimg.com/vi/7TWnR9acHWo/mqdefault.jpg', 'qigong,yi jin jing,tai chi,bio, energy');
