INSERT INTO `fun_video` VALUES(31, '8 Brocades of Qigong', 'http://www.youtube.com/watch?v=BQbfysOBxhI', 1014, '720', '8 Brocades of Chinese Qigong sometimes also called Chi Kung or Qi Gong, and also called Ba Duan Jin! Have fun, learn all eight, and then practice everyday about 5-10 breaths per brocade or posture! I love practicing Qigong in the morning first thing or right before bed!', 'English', 200728, 'Tutorial,Entertainment,Application', 'http://i.ytimg.com/vi/BQbfysOBxhI/mqdefault.jpg', 'Eight, Brocades, Qigong, Chinese, Chi, Kung, Qi, Gong, Jake, Mace, Tai,martial art, Yoga, Meditation, Traditional, Classical, China');
